import React, { useEffect } from 'react';
import '../css/header_index.css'; // Импорт CSS стилей
import banner from '../assetsImage/assets/img_2_728_d81a07.png'
import search from '../assetsImage/assets/search-svgrepo-com.svg'

const Header_index = () => {
  useEffect(() => {
    const inputField = document.getElementById('searchInput');
    inputField.addEventListener('focus', () => {
      inputField.removeAttribute('placeholder');
    });

    inputField.addEventListener('blur', () => {
      if (!inputField.value) {
        inputField.setAttribute('placeholder', 'Введите название продукта');
      }
    });

    const headerBlocks = document.querySelectorAll('.industrial-text-block');
    const mainImage = document.getElementById('header_image');
    let lastImage = '../assetsImage/assets/metalurgiya-glavnaya.jpg';

    headerBlocks.forEach((block) => {
      block.addEventListener('mouseover', () => {
        const blockId = block.id;
        changeImage(blockId);
      });

      block.addEventListener('mouseout', () => {
        mainImage.src = lastImage;
      });
    });

    function changeImage(blockId) {
      let newImage;
      if (blockId === 'image_header_1') {
        newImage = '../assetsImage/assets/metalurgiya-glavnaya.jpg';
      } else if (blockId === 'image_header_2') {
        newImage = 'assets/chemical.jpg';
      } else if (blockId === 'image_header_3') {
        newImage = 'assets/new.jpg';
      } else if (blockId === 'image_header_4') {
        newImage = 'assets/new (1).jpg';
      } else if (blockId === 'image_header_5') {
        newImage = 'assets/white_sedan_driving_on_the_highway_accross_the_forest_01.jpg';
      } else if (blockId === 'image_header_6') {
        newImage = 'assets/stroitelnaya_p2.jpg';
      }

      mainImage.style.opacity = '0';
      setTimeout(() => {
        mainImage.src = newImage;
        mainImage.style.opacity = '1';
        lastImage = newImage;
      }, 500);
    }

    return () => {
      inputField.removeEventListener('focus', () => {});
      inputField.removeEventListener('blur', () => {});
      headerBlocks.forEach((block) => {
        block.removeEventListener('mouseover', () => {});
        block.removeEventListener('mouseout', () => {});
      });
    };
  }, []);
    return (
        <header className="corporate-info-container1">
        <div className="corporate-section">
          <div className="company-info-container">
            <div className="header-navigation-bar">
              <div className="corporate-info-container">
                <div className="vertical-section-with-products">
                  <div className="company-info-logo">
                  <div className="company-news-section">
                    <p className="company-info-styles"><a href="">О компании</a></p>
                    <p className="company-info-styles"><a href="">Новости</a></p>
                    <p className="company-info-styles"><a href="">Партнерам</a></p>
                    <p className="company-info-styles"><a href="">Корпоративные политики</a></p>
                    <p className="company-info-styles"><a href="">Контакты</a></p>
                  </div>
                  <div className="logo_button_input">
                    <div className="logo_button">
                    <a href=""><img src={banner} className="banner-image" /></a>
                    <div className="button_product">
                      <form action="">
                        <button>
                          <p>
                            Продукты
                          </p>
                        </button>
                      </form>
                    </div>
                    </div>
                    <div className="product-input-container_search">
                    <form className="search" action="#0">
                      <input id="searchInput" className="field" name="search" type="text" placeholder="Введите название продукта" onfocus="clearPlaceholder(this)"/>
                      <button type="submit" className="search_button"><img src={search} alt=""/></button>
                    </form>
                    <script src="js/index.js" defer></script>
                     </div>
                  </div>
                </div>
                  <div className="column-layout-container">
                    <div className="hierarchical-text-container">
                      <div className="vertical-section-aside">
                        <p className="industry-description"><a href="tel:+7 888 777-88-55">+7 888 777-88-55</a></p>
                      </div>
                    </div>
                  </div>
                </div>
                <div className="partner-info-section">
                  <div className="industrial-product-categories-container">
                    <div id="image_header_1" className="industrial-text-block"><p><a href="">Металлургия и Машиностроение</a></p></div>
                    <div id="image_header_2" className="industrial-text-block"><p><a href="">Химическая и горнодобывающая промышленность</a></p></div>
                    <div id="image_header_3" className="industrial-text-block"><p><a href="">Сельское хозяйство</a></p></div>
                    <div id="image_header_4" className="industrial-text-block"><p><a href="">Коммерческий транспорт</a></p></div>
                    <div id="image_header_5" className="industrial-text-block"><p><a href="">Легковой автотранспорт</a></p></div>
                    <div id="image_header_6" className="industrial-text-block"><p><a href="">Строительная промышленность</a></p></div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </header>
      );
}

export default Header_index;
