import React, { useState } from 'react';
import HeaderIndex from './components/header_index';
import Index from './components/index';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import mainImage from '../assetsImage/assets/metalurgiya-glavnaya.jpg'; // Импортируйте изображения
import mainImage2 from '../assetsImage/assets/chemical.jpg';


const App = () => {

    const handleImageChange = (blockId) => {
        // Логика выбора изображения в зависимости от ID блока
        switch (blockId) {
          case 'image_header_1':
            setMainImage(mainImage);
            break;
          case 'image_header_2':
            setMainImage(mainImage2);
            break;
          // Добавляем остальные случаи для каждого блока
          default:
            setMainImage(mainImage); // Изображение по умолчанию
        }
      };

  return (
    <Router>
      <HeaderIndex handleImageChange={handleImageChange} />
      <Routes>
         <Route exact path="/" element={<Index />} />
      </Routes>
    </Router>
  );
};

export default App;
