import React, { useState } from 'react';
import HeaderIndex from './components/header_index';
import Index from './components/index';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import mainImage1 from '../assetsImage/assets/metalurgiya-glavnaya.jpg';
import mainImage2 from '../assetsImage/assets/chemical.jpg';
import mainImage3 from '../assetsImage/assets/new.jpg';
import mainImage4 from '../assetsImage/assets/new (1).jpg';
import mainImage5 from '../assetsImage/assets/white_sedan_driving_on_the_highway_accross_the_forest_01.jpg';
import mainImage6 from '../assetsImage/assets/stroitelnaya_p2.jpg';

const App = () => {
  const [mainImage, setMainImage] = useState(mainImage1);

  const handleImageChange = (blockId) => {
    switch (blockId) {
      case 'image_header_1':
        setMainImage(mainImage1);
        break;
      case 'image_header_2':
        setMainImage(mainImage2);
        break;
      case 'image_header_3':
        setMainImage(mainImage3);
        break;
      case 'image_header_4':
        setMainImage(mainImage4);
        break;
      case 'image_header_5':
        setMainImage(mainImage5);
        break;
      case 'image_header_6':
        setMainImage(mainImage6);
        break;
      default:
        setMainImage(mainImage1); // Если нет совпадений, используем mainImage1
    }
  };

  return (
    <Router>
      <HeaderIndex handleImageChange={handleImageChange}/>
      <Routes>
        <Route exact path="/" element={<Index mainImage={mainImage} />} />
      </Routes>
    </Router>
  );
};

export default App;
