import React, { useState } from 'react';
import HeaderIndex from './components/header_index';
import Index from './components/index';
import Footer from './components/footer'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import mainImage from './assetsImage/assets/metalurgiya-glavnaya.jpg'; // Импортируйте изображения
import mainImage2 from './assetsImage/assets/chemical.jpg';
import Contacts from './components/contacts'
import Header from './components/header'


const App = () => {

  return (
    <Router>
    {/* Условный рендеринг для заголовка */}
    <Routes>
        <Route
            path="/"
            element={
                <>
                    <HeaderIndex /> {/* Шапка для Index */}
                    <Route exact path="/" element={<Index />} />
                </>
            }
        />
        <Route
            path="/contacts"
            element={
                <>
                    <Header /> {/* Шапка для Contacts и других страниц */}
                    <Route exact path="/contacts" element={<Contacts />} />
                </>
            }
        />
        {/* Добавьте другие маршруты для других страниц */}
    </Routes>
    <Footer /> {/* Одинаковый футер для всех страниц */}
</Router>
  );
};

export default App;
