import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Index from './components/index';
import HeaderIndex from './components/header_index';
import Footer from './components/footer';

const App = () => {

    const [mainImage, setMainImage] = useState(null);

    const handleImageChange = (blockId) => {
        let newImage;
        if (blockId === 'image_header_1') {
          newImage = mainImage1;
        } else if (blockId === 'image_header_2') {
          newImage = mainImage2;
        } else if (blockId === 'image_header_3') {
          newImage = mainImage3;
        } else if (blockId === 'image_header_4') {
          newImage = mainImage4;
        } else if (blockId === 'image_header_5') {
          newImage = mainImage5;
        } else if (blockId === 'image_header_6') {
          newImage = mainImage6;
        }

      setMainImage(newImage);
    };

  return (
    <Router>
      <HeaderIndex handleImageChange={(blockId) => {}} />
      <Routes>
        <Route exact path="/" element={<Index />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
